import React, { useState } from "react";

const App = () => {
  const [number, setNumber] = useState(20);
  const [gift, setGift] = useState(1);

  const handleOlder = () => {
    setNumber(number + 1);
  };

  return (
    <>
      <span>Hoy tengo {number} años de edad" </span>
      <button type="button" onClick={handleOlder}>
        Hazme más viejo
        <p>Tengo un {gift}</p>
      </button>
    </>
  );
};

export default App;
