import React from "react";

class SheepCounter extends React.Component {
  constructor(props) {
    super(props);
    this.handlerSheep = this.handlerSheep.bind(this);
    this.state = {
      clickCount: 0,
    };
  }

  handlerSheep() {
    this.setState((prevState, props) => {
      let prevValue = prevState.clickCount;
      let nextCounter = prevValue + 1;
      return {
        clickCount: nextCounter,
      };
    });
  }

  render() {
    return (
      <>
        <h1>{this.state.clickCount} </h1>
        <button onClick={this.handlerSheep}> SHEEP COUNTER </button>
      </>
    );
  }
}

export default SheepCounter;
